  //           className="form-control form-control-lg"
  //           placeholder="Song title..."
  //           name="userInput"
  //           value={userInput}
  //           onChange={onChange}
  //         />
  //       </div>
  //       <button className="btn btn-primary btn-lg btn-block mb-5" type="submit">
  //         Get Track MovieDetails
  //       </button>
  //     </form>
  //   </div>
  // );
};

export default Search;
